files.rejectedCss:
#disqus_thread .post-content, 
#disqus_thread .post-body, 
#disqus_thread .post-message, 
#disqus_thread .comment__header, 
#disqus_thread .comment__footer, 
#disqus_thread .post-menu, 
#disqus_thread .comment-footer__menu {
  background: var(--color-background) !important;
  color: var(--color-text-secondary) !important;
  font-family: 'Montserrat', Arial, Helvetica, sans-serif !important;
  font-size: 1.08rem;
  line-height: 1.7;
}

#disqus_thread .comment__header, 
#disqus_thread .post-byline {
  color: var(--color-primary) !important;
  font-weight: 700;
  letter-spacing: 0.2px;
}

#disqus_thread .comment__footer a, 
#disqus_thread .post-menu a {
  color: var(--color-primary) !important;
  text-decoration: underline;
  font-weight: 600;
  letter-spacing: 0.2px;
}

#disqus_thread .icon-flag, 
#disqus_thread .icon-share, 
#disqus_thread .icon-twitter-x, 
#disqus_thread .icon-facebook {
  color: var(--color-primary) !important;
  opacity: 0.85;
}
#disqus_thread .post-menu, 
#disqus_thread .comment-footer__menu {
  background: transparent !important;
  border: none !important;
}
#disqus_thread .post-menu-item a, 
#disqus_thread .comment-footer__action {
  color: var(--color-primary) !important;
  background: none !important;
  border-radius: 6px;
  padding: 2px 8px;
  transition: background 0.2s;
}
#disqus_thread .post-menu-item a:hover, 
#disqus_thread .comment-footer__action:hover {
  background: var(--color-surface) !important;
  color: var(--color-text) !important;
}
#disqus_thread .post-message p {
  margin-bottom: 0.7em;
}
#disqus_thread .see-more {
  color: var(--color-primary) !important;
  font-weight: 600;
  cursor: pointer;
}
#disqus_thread .vote-up, 
#disqus_thread .vote-down {
  color: var(--color-primary) !important;
  background: none !important;
  border-radius: 4px;
  transition: background 0.2s;
}
#disqus_thread .vote-up:hover, 
#disqus_thread .vote-down:hover {
  background: var(--color-surface) !important;
  color: var(--color-text) !important;
}

.search-results-count {
  display: block;
  font-size: var(--font-size-xl);
  font-weight: var(--font-weight-medium);
  color: var(--color-text);
  margin: var(--spacing-lg) 0 var(--spacing-lg) 0;
  text-align: center;
  letter-spacing: var(--letter-spacing-subtle);
  font-family: var(--font-primary);
}
.search-input-wrapper {
  position: relative;
  width: 100%;
}
.search-input-icon {
  position: absolute;
  left: var(--spacing-xl);
  top: 50%;
  transform: translateY(-50%);
  font-size: var(--font-size-lg);
  color: var(--color-text-secondary);
  pointer-events: none;
  z-index: 10;
}
.search-input-wrapper .pagefind-ui__search-input {
  padding-left: calc(var(--spacing-xl) + var(--spacing-lg) + var(--spacing-lg));
}
